<?phpini_set('display_errors', 1);ini_set('display_startup_errors', 1);error_reporting(E_ALL);use PHPMailer\PHPMailer\PHPMailer;use PHPMailer\PHPMailer\SMTP;use PHPMailer\PHPMailer\Exception;//Load Composer's autoloaderrequire 'mailfn/vendor/autoload.php';//Create an instance; passing `true` enables exceptions$mail = new PHPMailer(true);try {    //Server settings    $mail->SMTPDebug = SMTP::DEBUG_SERVER;                      //Enable verbose debug output    $mail->isSMTP();                                            //Send using SMTP    $mail->Host       = 'mail.bloomhealthcare.in';                     //Set the SMTP server to send through    $mail->SMTPAuth   = true;                                   //Enable SMTP authentication    $mail->Username   = 'info@bloomhealthcare.in';                     //SMTP username    $mail->Password   = 'InfoBloom1974';                               //SMTP password    $mail->SMTPSecure = PHPMailer::ENCRYPTION_SMTPS;            //Enable implicit TLS encryption    $mail->Port       = '465';                                    //TCP port to connect to; use 587 if you have set `SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS`    //Recipients    $mail->setFrom('bccmarcom@gmail.com', 'Mailer');    $mail->addAddress('bccmarcom@gmail.com', 'Joe User');     //Add a recipient    $mail->addAddress('developers@brandcarecom.in');               //Name is optional    $mail->addReplyTo('bccmarcom@gmail.com', 'Information');    // $mail->addCC('cc@example.com');    // $mail->addBCC('bcc@example.com');    //Attachments    // $mail->addAttachment('/var/tmp/file.tar.gz');         //Add attachments    // $mail->addAttachment('/tmp/image.jpg', 'new.jpg');    //Optional name    //Content    $mail->isHTML(true);                                  //Set email format to HTML    $mail->Subject = 'Here is the subject';    $mail->Body    = 'This is the HTML message body <b>in bold!</b>';    $mail->AltBody = 'This is the body in plain text for non-HTML mail clients';    $mail->send();    echo 'Message has been sent';} catch (Exception $e) {    echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";}